MemUNITFrmItemApp:
unit Item@nameclassUnit;

interface

uses
  System.SysUtils, System.Types, System.UITypes, System.Classes,
  System.Variants,
  FMX.Types, FMX.Graphics, FMX.Controls, FMX.Forms, FMX.Dialogs, FMX.StdCtrls,
  FMX.Objects, FMX.Controls.Presentation, FMX.Layouts;

type
  TItem@nameclass = class(TFrame)
    @layoutallfields
    @labelallfields
    RectPlatForm: TRectangle;
    RecEdit: TRectangle;
    RecDelete: TRectangle;
    LayEdit: TLayout;
    LayDelete: TLayout;
    procedure RecDeleteClick(Sender: TObject);
    procedure RecEditClick(Sender: TObject);
  private
    { Private declarations }
    FID: string;
    FIsSelected: Boolean;
    procedure SetIsSelected(const Value: Boolean);
    procedure Delete@nameclass;
    procedure Edit@nameclass;
    { Private declarations }
  public
    constructor Create(AOwner: TComponent); override;
    Destructor Destroy; override;
    property IsSelected: Boolean read FIsSelected write SetIsSelected;
    property ID: string read FID write FID;

  var
    LoadData: Procedure of object;
    ShowDetail: Procedure(aID: string) of object;
  end;

implementation

{$R *.fmx}
{$WARN IMPLICIT_STRING_CAST OFF}
{$WARN IMPLICIT_STRING_CAST_LOSS OFF}

uses Controller@nameclass, Consts, @nameclasssUnit, DMUnit;
{ TItem@nameclass }

constructor TItem@nameclass.Create(AOwner: TComponent);
begin
  inherited;  
end;

procedure TItem@nameclass.Delete@nameclass;
var
  IsDeleted: Boolean;
begin

  with T@nameclass.Create(ID) do
    try
      IsDeleted := Delete;
    finally
      Free;
    end;
  if IsDeleted then
    DM.GS.ProcThrd(LoadData);
end;

destructor TItem@nameclass.Destroy;
begin
  inherited;
end;

procedure TItem@nameclass.Edit@nameclass;
begin
  ShowDetail(FID);
end;

procedure TItem@nameclass.RecDeleteClick(Sender: TObject);
begin
  Delete@nameclass;
end;

procedure TItem@nameclass.RecEditClick(Sender: TObject);
begin
  Edit@nameclass;
end;

procedure TItem@nameclass.SetIsSelected(const Value: Boolean);
begin
  FIsSelected := Value;
  if FIsSelected then
  begin
    RectPlatForm.Fill.Color := $FFA4B197;
  end
  else
  begin
    RectPlatForm.Fill.Color := Frm@nameclasss.aColor_Item; // Color_Item;
  end;

end;

end.
