    Timer1: TTimer;    
	LbWinTitle: TLabel;
	SBPlatForm: TScrollBox;
    BtnSave: TcxButton;
    BtnCancel: TcxButton;	
    @PANELS_UNIT
    @PROCEDURES_UNIT    
	@DefinitonBtnForgenKey
    procedure Timer1Timer(Sender: TObject);
    procedure BtnSaveClick(Sender: TObject);
    procedure BtnCancelClick(Sender: TObject);
    procedure FormShow(Sender: TObject);
    procedure FormDestroy(Sender: TObject);
	procedure SBPlatFormMouseWheel(Sender: TObject; Shift: TShiftState;
              WheelDelta: Integer; MousePos: TPoint; var Handled: Boolean);
  private
    @DefinitonProcedureLoadForgenKey
    function IsDataCurrect: Boolean;
    procedure Load@nameclass;
    function Save@nameclass: Boolean;
  public
  Var
    ID: string;
  end;

var
  FrmDetail@nameclass: TFrmDetail@nameclass;

implementation

{$R *.dfm}
{$WARN IMPLICIT_STRING_CAST OFF}
{$WARN IMPLICIT_STRING_CAST_LOSS OFF}

uses DmUnit, Controller@nameclass  @ControllerForgenKey  ;
procedure TFrmDetail@nameclass.SBPlatFormMouseWheel(Sender: TObject; Shift: TShiftState;
  WheelDelta: Integer; MousePos: TPoint; var Handled: Boolean);
begin
  if WheelDelta > 0 then
    TScrollBox(Sender).VertScrollBar.Position := TScrollBox(Sender)
      .VertScrollBar.Position - 10
  else
    TScrollBox(Sender).VertScrollBar.Position := TScrollBox(Sender)
      .VertScrollBar.Position + 10;
  Handled := true;
end;

procedure TFrmDetail@nameclass.BtnSaveClick(Sender: TObject);
begin
  if Save@nameclass then
    ModalResult := mrOk;
end;

procedure TFrmDetail@nameclass.BtnCancelClick(Sender: TObject);
begin
  ModalResult := mrCancel;
end;
procedure TFrmDetail@nameclass.FormDestroy(Sender: TObject);
begin
  // free
@freeitemsforgenkey
end;
procedure TFrmDetail@nameclass.FormShow(Sender: TObject);
begin
 @LoadForgenKeyOnShow
  Load@nameclass;
end;

function TFrmDetail@nameclass.IsDataCurrect: Boolean;
begin
//  Result := False;
//  if DM.GS.IsNotEmpty(EdName.Text) then
    Result := True;
end;

procedure TFrmDetail@nameclass.Load@nameclass;
begin
  with T@nameclass.Create(ID) do
    try
      @Fields_Load
    finally
      Free;
    end;
end;

function TFrmDetail@nameclass.Save@nameclass: Boolean;
begin
  with T@nameclass.Create(ID) do
    try
      @Fields_Save
      Result := Save;
    finally
      Free;
    end;
end;

procedure TFrmDetail@nameclass.Timer1Timer(Sender: TObject);
begin
  BtnSave.Enabled := IsDataCurrect;
end;

@Body_OnClick_Btn_EmptyImg
